mapper.insert:
insert into T_COMMON_SECTION (id,name,tunnel_id,store_id,area_id,total_cable_number,camera,start_point,end_point,crt_time) select COMMON_SECTION_SEQUENCE.nextval,a.* from (
select #{item.name}, #{item.tunnelId}, #{item.storeId}, #{item.areaId}, #{item.totalCableNumber}, #{item.camera}, #{item.startPoint}, #{item.endPoint}, #{item.crtTime} from dual
)a

mapper.select:
select * from T_COMMON_SECTION


		and id = #{id}


		and parent_id = #{parentId}


		and instr(name,#{name})>0



		and tunnel_id = #{tunnelId}


		and store_id = #{storeId}


		and area_id = #{areaId}


		and crt_time >= #{startTime}


		and crt_time <= #{endTime}


order by crt_time DESC

select t.id,t.name from t_common_section t


		and t.store_id = #{storeId}


		and t.area_id = #{areaId}


order by t.crt_time DESC
