mapper.insert:
insert into T_MAM_ALARM (ID, alarm_date,alarm_name,alarm_level,tunnel_id,object_id,object_name,is_distribute,description,cleaned,cleaned_date,alarm_source,longitude,latitude) select MAM_ALARM_SEQUENCE.nextval,a.* from (
select #{item.alarmDate}, #{item.alarmName}, #{item.alarmLevel}, #{item.tunnelId}, #{item.objectId}, #{item.objectName}, #{item.isDistribute}, #{item.description}, #{item.cleaned}, #{item.cleanedDate}, #{item.alarmSource}, #{item.longitude}, #{item.latitude} from dual
)a

mapper.select:
select * from T_MAM_ALARM


		and id = #{id}


		and alarm_date > #{startTime}


		and alarm_date < #{endTime}


		and alarm_level = #{alarmLevel}


		and tunnel_id = #{tunnelId}


		and object_id = #{objectId}


		and cleaned = #{cleaned}


		and is_distribute = #{isDistribute}


order by alarm_date desc

select t.object_id,count(t.object_id) as count_num from T_MAM_ALARM t
where t.alarm_date >= #{startTime} and t.alarm_date <=#{endTime}
group by t.object_id order by count_num desc

