mapper.insert:
insert into T_MAM_MEASOBJ (ID, tunnel_id,store_id,area_id,SECTION_ID, NAME, DATATYPE_ID, OBJTYPE_ID, ACTIVED, DESCRIPTION, LONGITUDE, LATITUDE, height, deviation) select a.* from (
select #{item.id,jdbcType=DECIMAL}, #{item.tunnelId,jdbcType=DECIMAL}, #{item.storeId,jdbcType=DECIMAL}, #{item.areaId,jdbcType=DECIMAL}, #{item.sectionId,jdbcType=DECIMAL}, #{item.name,jdbcType=VARCHAR}, #{item.datatypeId,jdbcType=DECIMAL}, #{item.objtypeId,jdbcType=DECIMAL}, #{item.actived,jdbcType=DECIMAL}, #{item.description,jdbcType=VARCHAR}, #{item.longitude,jdbcType=VARCHAR}, #{item.latitude,jdbcType=VARCHAR}, #{item.height,jdbcType=VARCHAR}, #{item.deviation,jdbcType=DECIMAL} from dual
)a

mapper.select:
select * from T_MAM_MEASOBJ


        and id = #{id,jdbcType=DECIMAL}


        and tunnel_id = #{tunnelId,jdbcType=DECIMAL}


        and store_id = #{storeId,jdbcType=DECIMAL}


        and area_id = #{areaId,jdbcType=DECIMAL}


        and SECTION_ID = #{sectionId,jdbcType=DECIMAL}


       and instr(name,#{name})>0


        and DATATYPE_ID = #{datatypeId,jdbcType=DECIMAL}


     and OBJTYPE_ID in 

         #{item}

select * from T_MAM_MEASOBJ


        and LONGITUDE <= #{longitude,jdbcType=VARCHAR}


        and LATITUDE <= #{latitude,jdbcType=VARCHAR}


        and height <= #{height,jdbcType=VARCHAR}

select * from T_MAM_MEASOBJ where id in

    #{item}

order by id asc
