mapper.select:
SELECT * FROM T_MAM_MEASVALUE_DASSpectrum

	
		and OBJECT_ID = #{objectId}
	
	
		and time >=#{startTime}
	
	
		and time <=#{endTime}

mapper.insert:
insert into T_MAM_MEASVALUE_DASSpectrum (TIME, OBJECT_ID, ZONE_ID, CHANNEL_COUNT, CHANNEL_ID, SAMPLERATE, FFT_SIZE) select a.* from (
select #{item.time,jdbcType=TIMESTAMP}, #{item.objectId,jdbcType=DECIMAL}, #{item.zoneId,jdbcType=DECIMAL}, #{item.channelCount,jdbcType=DECIMAL}, #{item.channelId,jdbcType=DECIMAL}, #{item.samplerate,jdbcType=DECIMAL}, #{item.fftSize,jdbcType=DECIMAL} from dual
)a
