mapper.select:
SELECT
SUM(VALUE) FROM T_OAM_ENERGY
WHERE TUNNEL_ID = #{id}

SELECT SUM(VALUE) FROM T_OAM_ENERGY

	
		and tunnel_id = #{tunnelId}
	
	
		and crt_time >=
		#{startTime}
	
	
		and crt_time <=
		#{endTime}

select sum(value) from T_OAM_ENERGY

	
		and crt_time  >= 
		#{startTime}
	
	
		and crt_time  <= 
		#{endTime}

select e.tunnel_id ,t.name as tunnelName, sum(e.value) as totalValue,avg(e.unit_price) as avgUnitPrice from T_OAM_ENERGY e left join t_common_tunnel t on e.tunnel_id=t.id
and e.crt_time >= #{startTime}
and e.crt_time <= #{endTime}
group by e.tunnel_id,t.name

select sum(value) from T_OAM_ENERGY where tunnel_id = #{id}
and
crt_time >=
#{startTime} and crt_time <=
#{endTime}

mapper.insert:
insert into T_OAM_ENERGY (id,tunnel_id,object_type,value,unit_price,crt_time) select OAM_ENERGY_SEQUENCE.nextval,a.* from (
select #{item.tunnelId}, #{item.objectType}, #{item.value}, #{item.unitPrice}, #{item.crtTime} from dual
)a

mapper.select:

	select e.*,t.name as tunnelName from T_OAM_ENERGY e
	left join t_common_tunnel t on e.tunnel_id = t.id
	
		
			and t.id = #{tunnelId}
		
		
			and e.crt_time >= #{startTime}
		
		
			and e.crt_time <= #{endTime}
		
	
	order by e.crt_time desc

select e.*,t.name as tunnelName 
from T_OAM_ENERGY e 
left join t_common_tunnel t on t.id = e.tunnel_id

	
		and e.tunnel_id = #{tunnelId}
	
	
		and e.object_type = #{objectType}
	
	
		and e.crt_time >= #{startTime}
	
	
		and e.crt_time <= #{endTime}
	

order by e.crt_time desc
