mapper.select:
select c.id,c.name,c.position,c.tunnel_id,t.name as
tunnelName,c.crt_time from
T_OMM_CHECKINGPOINT c
left join
T_COMMON_TUNNEL t on c.tunnel_id = t.id

	
		and c.id = #{id}
	
	
		and instr(c.name,#{name,jdbcType=VARCHAR})>0
	
	
		and c.tunnel_id = #{tunnelId}
	
	
		and c.crt_time >=
		#{startTime}
	
	
		and c.crt_time <=
		#{endTime}

mapper.insert:
insert into T_OMM_CHECKINGPOINT (id,name,position,tunnel_id,crt_time) select OMM_CHECKINGPOINT_SEQUENCE.nextval,a.* from (
select #{item.name}, #{item.position}, #{item.tunnelId}, #{item.crtTime} from dual
)a
