mapper.insert:
insert into t_omm_instrument (name,model_id,type_id,vender_id,use_status,status,in_time) select a.* from (
select #{item.name}, #{item.modelId}, #{item.typeId}, #{item.venderId}, #{item.useStatus}, #{item.status}, #{item.inTime} from dual
)a

insert into t_omm_instrument (spare_id,name,model_id,type_id,vender_id,use_status,status,in_time) select a.* from (
select #{item.spareId}, #{item.name}, #{item.modelId}, #{item.typeId}, #{item.venderId}, #{item.useStatus}, #{item.status}, #{item.inTime} from dual
)a

mapper.update:
		update t_omm_instrument
        set status = #{status}
		where id in
		
            #{item}

		update t_omm_instrument
        set status = #{in.status},
        	use_status = #{in.useStatus}
		where id in
		
            #{item}

mapper.select:
select a.*, b.name as model_name, c.name as type_name, d.name as vender_name
from t_omm_instrument a
LEFT JOIN t_omm_equipment_model b ON a.model_id = b.id
LEFT JOIN t_omm_equipment_type c ON a.type_id = c.id
LEFT JOIN t_omm_equipment_vender d ON a.vender_id = d.id

	
        and a.spare_id is not null
    
    
        and instr(a.name,#{name})>0
    
    
        and a.model_id = #{modelId}
    
    
        and a.type_id = #{typeId}
    
    
        and a.vender_id = #{venderId}
    
    
        and a.use_status = #{useStatus}
    
    
        and a.status = #{status}
    
    
        and a.in_time >=#{startTime}
    
    
        and a.in_time <=#{endTime}

mapper.delete:
    	delete from t_omm_instrument 
		WHERE ID IN
		
            #{item}
